    private void botaopalpiteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botaopalpiteActionPerformed
        int pc = (int) (1 + Math.random() * (5-1));
        int usu = Integer.parseInt(numerousuario.getValue().toString());
        resposta.setText(Integer.toString(pc));
        usuario.setText(Integer.toString(usu));
        String resul = pc==usu?"Acertou!":"Errou!";
        resultado.setText(resul);
    }//GEN-LAST:event_botaopalpiteActionPerformed

